  const binaryData = item.binary.data;

  // Create a new binary object with modified properties
  const modifiedBinary = {
    ...binaryData,
    mimeType: 'audio/mpeg',  // Try this MIME type instead
    fileExtension: 'mp3',
    fileName: `${item.json.Username || 'audio'}.mp3`,
    contentType: 'audio/mpeg'  // Add content type explicitly
  };

  return {
    json: {
      ...item.json,
    },
    binary: {
      audio: modifiedBinary  // Change the key to 'audio' instead of 'data'
    }
  };
});

// Get the JSON data from the specified path
const originalScript = $input.first().json["Original Script"];
const recordID = $input.item.json["id"];

// Check if the field exists
if (originalScript === undefined) {
  return [{ error: "Could not find 'Original script' field in the JSON data" }];
} else {
  // Convert to string if it's not already a string
  const scriptString = typeof originalScript === 'string' 
    ? originalScript 
    : JSON.stringify(originalScript);

  // Count the characters
  const characterCount = scriptString.length;

  // Create the query string with the character count
  const query = `Repurpose the original script in no more or less than ${characterCount} characters. Here is the Record ID: ${recordID}`;

  // Return the result as an array with a single object containing the query
  return [
    { query: query }
  ];
}

